import { useCart } from "../../Context/CartContext";
import { useState, useEffect } from "react";
import './Checkout.css';

export default function Checkout() {
  const { cartItems } = useCart();
  const BASE_URL = "http://127.0.0.1:8000";
  const shippingCost = 500;

  const [form, setForm] = useState({
    firstName: "",
    lastName: "",
    phone: "",
    wilaya: "",
    city: "",
    address: "",
    email: "",
    notes: ""
  });

  const [wilayas, setWilayas] = useState([]);
  const [cities, setCities] = useState([]);

  useEffect(() => {
    fetch("https://raw.githubusercontent.com/AbderrahmeneDZ/Wilaya-Of-Algeria/refs/heads/master/Commune_Of_Algeria.json")
      .then(res => res.json())
      .then(data => {
        const unique = [
          ...new Map(data.map(item => [
            item.wilaya_name.code,
            {
              code: item.wilaya_name.code,
              name: item.wilaya_name.name,
              ar_name: item.wilaya_name.ar_name
            }
          ])).values()
        ];
        setWilayas(unique);
      });
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));

    if (name === "wilaya") {
      fetch("https://raw.githubusercontent.com/AbderrahmeneDZ/Wilaya-Of-Algeria/refs/heads/master/Commune_Of_Algeria.json")
        .then(res => res.json())
        .then(data => {
          const filtered = data.filter(item => item.wilaya_name.name === value);
          const communes = [...new Set(filtered.map(i => i.commune_name))];
          setCities(communes);
        });
    }
  };

  const total = cartItems.reduce((sum, item) => {
    return sum + parseFloat(item.price) * item.quantity;
  }, 0) + shippingCost;

  const handleSubmit = () => {
    alert("Order placed!");
    console.log(form, cartItems);
  };

  return (
    <div className="checkout-container">
      {/* LEFT - Billing Form */}
      <div className="checkout-form">
        <h2>Billing & Shipping</h2>

        <div className="form-row">
          <input
            name="firstName"
            placeholder="First name (optional)"
            onChange={handleChange}
          />
          <input
            name="lastName"
            placeholder="Last name (optional)"
            onChange={handleChange}
          />
        </div>

        <input
          name="phone"
          placeholder="Phone *"
          required
          onChange={handleChange}
        />

        <input disabled value="Algeria" />

        <select name="wilaya" required onChange={handleChange}>
          <option value="">Select Wilaya *</option>
          {wilayas.map(w => (
            <option key={w.code} value={w.name}>
              {w.code} {w.name} - {w.ar_name}
            </option>
          ))}
        </select>

        <select name="city" required onChange={handleChange}>
          <option value="">Select City *</option>
          {cities.map((city, idx) => (
            <option key={idx} value={city}>{city}</option>
          ))}
        </select>

        <input
          name="address"
          placeholder="Street address (optional)"
          onChange={handleChange}
        />
        <input
          name="email"
          type="email"
          placeholder="Email address (optional)"
          onChange={handleChange}
        />

        <textarea
          name="notes"
          placeholder="Order notes (optional)"
          onChange={handleChange}
        />
      </div>

      {/* RIGHT - Order Summary */}
      <div className="checkout-summary">
        <h2>Your Order</h2>

        {cartItems.map((item) => (
          <div key={item.id} className="checkout-item">
            <img src={BASE_URL + (item.images?.[0] || item.image)} alt={item.title} />
            <div>
              <strong>{item.title}</strong> × {item.quantity}
            </div>
            <div>{(parseFloat(item.price) * item.quantity).toFixed(2)} د.ج</div>
          </div>
        ))}

        <div className="checkout-totals">
          <div>Subtotal: {cartItems.reduce((sum, item) => sum + parseFloat(item.price) * item.quantity, 0).toFixed(2)} د.ج</div>
          <div>Shipping: {shippingCost.toFixed(2)} د.ج</div>
          <div><strong>Total: {total.toFixed(2)} د.ج</strong></div>
        </div>

        <p>Pay in Person</p>

        <button onClick={handleSubmit} className="checkout-button">PLACE ORDER</button>
      </div>
    </div>
  );
}
