import { Link } from "react-router-dom";
import "./Header.css";
import { useState } from "react";
import { useCart } from "../../src/Context/CartContext";

export default function Navbar() {
  const [showCart, setShowCart] = useState(false);
  const { cartItems, removeFromCart } = useCart();
  const BASE_URL = "http://127.0.0.1:8000";

  return (
    <header className="navbar">
      <div className="navbar-container">
        <Link to="/" className="navbar-logo">üõçÔ∏è MyStore</Link>

<div className="navbar-right">
  <nav className="navbar-links">
    <Link to="/">Home</Link>
    <Link to="/dashboard">Dashboard</Link>
    <Link to="/products">Products</Link>
  </nav>

  <button onClick={() => setShowCart(!showCart)} className="cart-button">
    <i className="fa-solid fa-cart-shopping"></i>
    {cartItems.length > 0 && (
      <span className="cart-badge">{cartItems.length}</span>
    )}
  </button>

  {showCart && (
    <div className="CartDev">
      {cartItems.length === 0 ? (
        <p>üõí Your cart is empty</p>
      ) : (
        <div>
          {cartItems.map((item, index) => (
            <div key={`${item.id}-${index}`} style={{ display: "flex", alignItems: "center", marginBottom: "10px" }}>
              <img
                src={"http://127.0.0.1:8000" + (item.images?.[0] || item.image)}
                alt={item.title}
                style={{ width: "50px", height: "50px", objectFit: "cover", borderRadius: "5px", marginRight: "10px" }}
              />
              <span style={{ flex: 1 }}>{item.title}</span>
              <button
                onClick={() => removeFromCart(item.id)}
                style={{ marginLeft: "10px", background: "red", color: "white", border: "none", padding: "4px 8px", borderRadius: "4px" }}
              >
                ‚ùå
              </button>
            </div>
          ))}
          <Link to="/cart" style={{ display: "block", color: "#fff", textAlign: "center", marginTop: "10px", textDecoration: "underline" }}>
            Go to Cart
          </Link>
        </div>
      )}
    </div>
  )}
</div>

      </div>
    </header>
  );
}
