import { Link } from "react-router-dom";
import "./Header.css";
import { useState } from "react";
import { useCart } from "../../src/Context/CartContext";

export default function Navbar() {
  const [showCart, setShowCart] = useState(false);
  const { cartItems, removeFromCart } = useCart();
  const BASE_URL = "http://127.0.0.1:8000";

  return (
    <header className="navbar">
  <div className="navbar-container" style={{ position: "relative" }}>
    {/* ...navigation... */}
    <button onClick={() => setShowCart(!showCart)} className="cart-button">
      <i className="fa-solid fa-cart-shopping"></i>
      {cartItems.length > 0 && (
        <span className="cart-badge">{cartItems.length}</span>
      )}
    </button>

    {/* üëá Make sure this is inside navbar-container, not outside */}
    {showCart && (
      <div className="CartDev">
        {cartItems.length === 0 ? (
          <p style={{ color: "white" }}>üõí Your cart is empty</p>
        ) : (
          <div>
            {cartItems.map((item) => (
              <div key={item.id} style={{ display: "flex", marginBottom: "10px", color: "white" }}>
                <img
                  src={"http://127.0.0.1:8000" + (item.images?.[0] || item.image)}
                  alt={item.title}
                  style={{
                    width: "50px",
                    height: "50px",
                    objectFit: "cover",
                    borderRadius: "5px",
                    marginRight: "10px"
                  }}
                />
                <span style={{ flex: 1 }}>{item.title}</span>
                <button
                  onClick={() => removeFromCart(item.id)}
                  style={{
                    background: "red",
                    color: "white",
                    border: "none",
                    padding: "4px 8px",
                    borderRadius: "4px",
                    cursor: "pointer"
                  }}
                >
                  ‚ùå
                </button>
              </div>
            ))}
            <Link to="/cart" style={{ display: "block", textAlign: "center", color: "white", marginTop: "10px" }}>
              Go to Cart
            </Link>
          </div>
        )}
      </div>
    )}
  </div>
</header>

  );
}
