import { useEffect, useState } from "react";

export default function Orders() {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8000/api/order/show") // Make sure this is the correct route!
      .then(res => res.json())
      .then(data => {
        console.log("‚úÖ Orders fetched:", data);
        setOrders(data);
      })
      .catch(err => console.error("Failed to load orders", err));
  }, []);

  return (
    <div style={{ padding: "2rem" }}>
      <h1>üì¶ All Orders</h1>

      {orders.length === 0 ? (
        <p>No orders found.</p>
      ) : (
        orders.map(order => (
          <div key={order.id} style={{
            border: "1px solid #ccc",
            borderRadius: "8px",
            padding: "1rem",
            marginBottom: "1rem"
          }}>
            <h3>üÜî Order ID: {order.id}</h3>
            <p>üë§ <strong>Name:</strong> {order.user_name}</p>
            <p>üìû <strong>Phone:</strong> {order.phone || "-"}</p>
            <p>üìß <strong>Email:</strong> {order.email || "-"}</p>
            <p>üìç <strong>Address:</strong> {order.user_address}</p>
            <p>üìù <strong>Notes:</strong> {order.notes || "No notes"}</p>
            <p>üìÖ <strong>Date:</strong> {new Date(order.createdAt).toLocaleString()}</p>
            <p>üöö <strong>Status:</strong> {order.status}</p>

            <h4>üõí Items:</h4>
            {Array.isArray(order.cartItems) && order.cartItems.length > 0 ? (
              <ul>
                {order.cartItems.map((item, idx) => (
                  <li key={idx}>
                    {item.title} √ó {item.quantity} ‚Äî {parseFloat(item.price).toFixed(2)} ÿØ.ÿ¨ each
                  </li>
                ))}
              </ul>
            ) : (
              <p>No items</p>
            )}
          </div>
        ))
      )}
    </div>
  );
}
