import { useEffect, useState } from "react";

export default function Orders() {
  const [orders, setOrders] = useState([]);

  const fetchOrders = () => {
    fetch("http://localhost:8000/api/order/all")
      .then(res => res.json())
      .then(data => setOrders(data))
      .catch(err => console.error("Failed to load orders", err));
  };

  useEffect(() => {
    fetchOrders();
  }, []);

  const updateStatus = async (id, status) => {
    try {
      const res = await fetch(`http://localhost:8000/api/order/${id}/status`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ status })
      });

      const data = await res.json();
      if (res.ok) {
        alert(`✅ Order ${status}`);
        fetchOrders();
      } else {
        alert(`❌ Error: ${data.message}`);
      }
    } catch (err) {
      console.error("❌ Failed to update order", err);
    }
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h1>📦 All Orders</h1>

      {orders.length === 0 ? (
        <p>No orders found.</p>
      ) : (
        orders.map(order => (
          <div key={order.id} style={{
            border: "1px solid #ccc",
            borderRadius: "8px",
            padding: "1rem",
            marginBottom: "1rem"
          }}>
            <h3>🆔 Order ID: {order.id}</h3>
            <p>👤 <strong>Name:</strong> {order.user_name}</p>
            <p>📞 <strong>Phone:</strong> {order.phone || "-"}</p>
            <p>📧 <strong>Email:</strong> {order.email || "-"}</p>
            <p>📍 <strong>Address:</strong> {order.user_address}</p>
            <p>📝 <strong>Notes:</strong> {order.notes || "No notes"}</p>
            <p>📅 <strong>Date:</strong> {new Date(order.createdAt).toLocaleString()}</p>
            <p>🚚 <strong>Status:</strong> {order.status}</p>

            <h4>🛒 Items:</h4>
            <ul>
              {order.cartItems?.map((item, idx) => (
                <li key={idx}>
                  {item.title} × {item.quantity} — {parseFloat(item.price).toFixed(2)} د.ج each
                </li>
              ))}
            </ul>

            {order.status === "pending" && (
              <div style={{ marginTop: "1rem" }}>
                <button
                  style={{ marginRight: "1rem", background: "#4CAF50", color: "#fff", padding: "0.5rem" }}
                  onClick={() => updateStatus(order.id, "accepted")}
                >
                  ✅ Accept
                </button>
                <button
                  style={{ background: "#f44336", color: "#fff", padding: "0.5rem" }}
                  onClick={() => updateStatus(order.id, "refused")}
                >
                  ❌ Refuse
                </button>
              </div>
            )}
          </div>
        ))
      )}
    </div>
  );
}
