import { useState } from "react";
import '../Dashboard/Products/Form.css';
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";

export default function CardProduct(){

    // const { id } = useParams(); // Get product ID from the route
    const [username, setUsername] = useState('');
    const [useraddress, setUseraddress] = useState('');

        const nav = useNavigate();


async function Submit(e){
  e.preventDefault();

  try {
    const formData = new FormData();
    formData.append('user_name', username);
    formData.append('user_address', useraddress);

    console.log("Sending:", { username, useraddress });

    const res = await axios.put(`http://127.0.0.1:8000/api/order/create/`, formData);
    console.log("✅ Product Updated:", res.data);
        

  } catch (err) {
    console.error("❌ Submit failed:", err);
  }
}



    
    return (

    <div className="form-container">
      <form onSubmit={Submit} className="product-form">

        <label htmlFor="username">Add Your Full Name</label>
        <input type="text" id="username" placeholder="Enter Your Full name" value={username} onChange={(e) => setUsername(e.target.value)} />

        <label htmlFor="useraddress">Add Your Address</label>
        <input type="text" id="desc" placeholder="Add Your Address" value={useraddress} onChange={(e) => setUseraddress(e.target.value)} />

        <button type="submit">CreateOrder</button>
      </form>
    </div>

    )
}
