import { useEffect, useState } from "react";

export default function Orders() {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8000/api/order/show")
      .then(res => res.json())
      .then(data => setOrders(data))
      .catch(err => console.error("Failed to load orders", err));
  }, []);

  return (
    <div style={{ padding: "2rem" }}>
      <h1>📦 All Orders</h1>

      {orders.length === 0 ? (
        <p>No orders found.</p>
      ) : (
        orders.map(order => (
          <div
            key={order.id}
            style={{
              border: "1px solid #ccc",
              borderRadius: "8px",
              padding: "1rem",
              marginBottom: "1.5rem",
              backgroundColor: "#f9f9f9"
            }}
          >
            <h3>🆔 Order ID: {order.id}</h3>
            <p><strong>👤 Name:</strong> {order.user_name || "-"}</p>
            <p><strong>📞 Phone:</strong> {order.phone || "-"}</p>
            <p><strong>📧 Email:</strong> {order.email || "-"}</p>
            <p><strong>📍 Address:</strong> {order.user_address || "-"}</p>
            <p><strong>📝 Notes:</strong> {order.notes || "No notes"}</p>
            <p><strong>📅 Date:</strong> {new Date(order.createdAt).toLocaleString()}</p>
            <p><strong>🚚 Status:</strong> {order.status}</p>

            <h4>🛒 Items:</h4>
            <ul style={{ paddingLeft: "1.5rem" }}>
              {order.cartItems?.map((item, idx) => (
                <li key={idx} style={{ marginBottom: "6px" }}>
                  <span style={{ fontWeight: 500 }}>{item.title}</span> × {item.quantity} 
                  {" — "}
                  <span style={{ color: "green" }}>{parseFloat(item.price).toFixed(2)} د.ج</span> each
                </li>
              ))}
            </ul>
          </div>
        ))
      )}
    </div>
  );
}
