import { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import "./Card.css"; // your styled file

export default function CardProduct() {
  const BASE_URL = "http://127.0.0.1:8000";
  const { id } = useParams();
  const [viewCard, setViewCard] = useState({});
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get(`http://127.0.0.1:8000/api/product/show/${id}`);
        setViewCard(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchData();
  }, [id]);

  const images = viewCard.images || (viewCard.image ? [viewCard.image] : []);

  const handleNext = () => {
    setCurrentImageIndex((prev) => (prev + 1) % images.length);
  };

  const handlePrev = () => {
    setCurrentImageIndex((prev) =>
      prev === 0 ? images.length - 1 : prev - 1
    );
  };

  return (
    <div className="product-view-container">
      <div className="product-left">
        <div className="image-slider">
          <button className="arrow left" onClick={handlePrev}>
            &lt;
          </button>

          <img
            src={BASE_URL + images[currentImageIndex]}
            alt=""
            className="product-main-image"
          />

          <button className="arrow right" onClick={handleNext}>
            &gt;
          </button>
        </div>
      </div>

      <div className="product-right">
        <p className="product-label">Perfume</p>
        <h1 className="product-title">{viewCard.title}</h1>
        <p className="product-description">{viewCard.description}</p>
        <div className="product-price">
          <span className="current-price"></span>
        </div>
        <button className="add-to-cart-button">ðŸ›’ Add to Cart</button>
      </div>
    </div>
  );
}
