import { useState } from "react";
import './Form.css';
import axios from "axios";

export default function CreateProducts(){


    const [title, setTitle] = useState('');
    const [desc, setDesc] = useState('');
    const [image, setImage] = useState(null);



    async function Submit(e){
          e.preventDefault();

        try {
            const formData = new FormData();
            formData.append('title', title);
            formData.append('description', desc);
            formData.append('image', image);
            
            let res = await axios
                .post("http://127.0.0.1:8000/api/product/create", formData
                )
                    console.log("Sending:", { title, desc, image });
                
        }catch(err){
            console.log(err);
        }
    }


    
    return (

    <div className="form-container">
      <form onSubmit={Submit} className="product-form">

        <label htmlFor="title">Title</label>
        <input type="text" id="title" placeholder="Enter Title" value={title} onChange={(e) => setTitle(e.target.value)} />

        <label htmlFor="desc">Description</label>
        <input type="text" id="desc" placeholder="Description" value={desc} onChange={(e) => setDesc(e.target.value)} />

        <label htmlFor="image">Upload Image</label>
        <input type="file" id="image" onChange={(e) => setImage(e.target.files?.item(0))} />

        <button type="submit">CreateProduct</button>
      </form>
    </div>

    )
}
