import { useEffect, useState } from "react";
import axios from "axios";
import Navbar from "../../components/Header";
import "./Products.css";
import { useNavigate } from "react-router-dom";
import { useCart } from "../../Context/CartContext";

export default function Products() {
  const { addToCart } = useCart();

  const [products, setProducts] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const BASE_URL = "http://127.0.0.1:8000";

  const nav = useNavigate();

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const res = await axios.get("http://127.0.0.1:8000/api/product/show");
        setProducts(res.data);
      } catch (err) {
        console.error("Failed to fetch products:", err);
      }
    };

    fetchProducts();
  }, []);

  // Filter products by search query
  const filteredProducts = products.filter(product =>
    product.title?.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <>
      <Navbar />
      <h1 className="page-title">Products</h1>

      <div style={{ textAlign: "center", marginBottom: "1rem" }}>
        <input
          type="text"
          placeholder="üîç Search products..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          style={{
            padding: "0.6rem",
            width: "80%",
            maxWidth: "400px",
            fontSize: "1rem",
            borderRadius: "8px",
            border: "1px solid #ccc"
          }}
        />
      </div>

      <div className="Dash-product-grid">
        {filteredProducts.length === 0 ? (
          <p style={{ textAlign: "center" }}>‚ùå No products found.</p>
        ) : (
          filteredProducts.map(product => (
            <div className="Dash-product-card" key={product.id}>
              <img
                src={
                  product.images?.length > 0
                    ? BASE_URL + product.images[0]
                    : product.image
                    ? BASE_URL + product.image
                    : "/default.jpg"
                }
                alt={product.title}
                className="Dash-product-image"
                onClick={() => nav(`/products/${product.id}`)}
              />
              <div className="Dash-product-info">
                <h2 className="Dash-product-title">{product.title}</h2>
                <p className="Dash-product-desc">{product.description}</p>
                {product.price !== undefined && (
                  <h1 className="current-price">
                    {parseFloat(product.price).toFixed(2)} DA
                  </h1>
                )}
              </div>
              <button
                className="add-to-cart-button"
                onClick={() => addToCart(product)}
              >
                üõí Add to Cart
              </button>
            </div>
          ))
        )}
      </div>
    </>
  );
}
