import axios from "axios"
import { useEffect, useState } from "react"
import { useParams } from "react-router-dom"
import './Card.css'

export default function CardProduct() {
  const BASE_URL = "http://127.0.0.1:8000";

  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [images, setImage] = useState('');

  const { id } = useParams();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get(`http://127.0.0.1:8000/api/product/show/${id}`);
        setTitle(res.data.title);
        setDescription(res.data.description);
        setImage(res.data.images);
      } catch (err) {
        console.log(err);
      }
    };
    fetchData();
  }, [id]);

  return (
    <div className="view-card-container">
      <div className="view-card">
        <img src=
        {
              product.images?.length > 0
              ? BASE_URL + product.images[0]
              : product.image
              ? BASE_URL + product.image
              : "/default.jpg" // fallback if no image at all        
              }

         alt={title} className="view-card-image" />
        <div className="view-card-info">
          <h1 className="view-card-title">{title}</h1>
          <p className="view-card-desc">{description}</p>
        </div>
      </div>
    </div>
  );
}
