import { useEffect, useState } from "react";
import axios from "axios";
import Navbar from "../../components/Header";
import "./Products.css"; // Add this line
import { Link, useNavigate, useParams } from "react-router-dom";


export default function Products() {
  const [products, setProducts] = useState([]);
  const BASE_URL = "http://127.0.0.1:8000";

  const { id } = useParams()


  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const res = await axios.get("http://127.0.0.1:8000/api/product/show");
        setProducts(res.data);
      } catch (err) {
        console.error("Failed to fetch products:", err);
      }
    };

    fetchProducts();
  }, []);

  const nav = useNavigate();
  

  return (
    <>
      <Navbar />
      <h1 className="page-title">Products</h1>


      <div className="Dash-product-grid">

              {products.map(product => (
          <div className="Dash-product-card" key={product.id}>
        <img
          src={BASE_URL + product.image}
          alt={product.title}
          className="Dash-product-image"
          onClick={()=> nav(`/products/${product.id}`)}
        />
            <div className="Dash-product-info">
              <h2 className="Dash-product-title">{product.title}</h2>
              <p className="Dash-product-desc">{product.description}</p>
            </div>

            <Link to={`/products/${product.id}`}>
                <button className="buyButton">Add To Cart</button>
            </Link>

          </div>

        ))}
                </div>

    </>
  );
}
