import axios from "axios"
import { useEffect, useState } from "react"
import { useParams } from "react-router-dom"
import './Card.css'

export default function CardProduct() {
  const BASE_URL = "http://127.0.0.1:8000";

  const [viewCard, setViewCard] = useState([]);
  // const [description, setDescription] = useState('');
  // const [images, setImage] = useState('');

  const { id } = useParams();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get(`http://127.0.0.1:8000/api/product/show/${id}`);
        setViewCard(res.data);
        
      } catch (err) {
        console.log(err);
      }
    };
    fetchData();
  }, [id]);


  return (

<div className="product-view-container">
  <div className="product-left">
    <img
      src={BASE_URL + (viewCard.images?.[0] || viewCard.image || "/default.jpg")}
      alt={viewCard.title}
      className="product-main-image"
    />
  </div>

  <div className="product-right">
    <span className="product-label">PERFUME</span>
    <h1 className="product-title">{viewCard.title}</h1>
    <p className="product-description">{viewCard.description}</p>

    <div className="product-price">
      <span className="current-price">$12.95</span>
      <span className="old-price">$18.95</span>
    </div>

    <button className="add-to-cart-button">
      <i className="fa fa-shopping-bag" style={{ marginRight: '8px' }}></i>
      Add to Cart
    </button>
  </div>
</div>

  );
}
