import { Link } from "react-router-dom";
import "./Header.css";
import { useState } from "react";
import { useCart } from "../../src/Context/CartContext"; // <-- Make sure this exists

export default function Navbar() {
  const [showCart, setShowCart] = useState(false);
  const { cartItems } = useCart(); // Optional: show items count or list

  return (
    <header className="navbar">
      <div className="navbar-container">
        {/* Logo */}
        <Link to="/" className="navbar-logo">
          üõçÔ∏è MyStore
        </Link>

        {/* Navigation Links */}
        <nav className="navbar-links">
          <Link to="/">Home</Link>
          <Link to="/dashboard">Dashboard</Link>
          <Link to="/products">Products</Link>

          {/* Cart Icon */}
          <button
            onClick={() => setShowCart(!showCart)}
            className="cart-button"
          >
            <i className="fa-solid fa-cart-shopping"></i>
            {cartItems.length > 0 && (
              <span className="cart-badge">{cartItems.length}</span>
            )}
          </button>
        </nav>
      </div>

      {/* Cart Panel */}
      {showCart && (
        <div className="CartDev">
          {cartItems.length === 0 ? (
            <p style={{ color: "white" }}>üõí Your cart is empty</p>
          ) : (
            <ul>
              {cartItems.map((item, idx) => (
                <li key={idx} style={{ color: "white" }}>
                  {item.title} - {parseFloat(item.price).toFixed(2)} DA
                </li>
              ))}
            </ul>
          )}
        </div>
      )}
    </header>
  );
}
