import axios from "axios"
import { useEffect, useState } from "react"
import { useParams } from "react-router-dom"
import './Card.css'

export default function CardProduct() {
  const BASE_URL = "http://127.0.0.1:8000";

  const [viewCard, setViewCard] = useState([]);
  // const [description, setDescription] = useState('');
  // const [images, setImage] = useState('');

  const { id } = useParams();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get(`http://127.0.0.1:8000/api/product/show/${id}`);
        setViewCard(res.data);
        
      } catch (err) {
        console.log(err);
      }
    };
    fetchData();
  }, [id]);


  return (

    <div className="view-card-container">
      
      <div className="">
{viewCard.images?.length > 0 ? (
  viewCard.images.map((img, index) => (
    <img
      key={index}
      src={BASE_URL + img}
      alt={`${viewCard.title} ${index + 1}`}
      className="view-card-image"
    />
  ))
) : viewCard.image ? (
  <img
    src={BASE_URL + viewCard.image}
    alt={viewCard.title}
    className="view-card-image"
  />
) : (
  <img
    src="/default.jpg"
    alt="default"
    className="view-card-image"
  />
)}

        <div className="view-card-info">
          <h1 className="view-card-title">{viewCard.title}</h1>
          <p className="view-card-desc">{viewCard.description}</p>
        </div>
      </div>
    </div>
  );
}
