import { useEffect, useState } from "react";
import axios from "axios";
import Navbar from "../../components/Header";
import "./Products.css"; // Add this line
import { Link } from "react-router-dom";

export default function Products() {
  const [products, setProducts] = useState([]);
  const BASE_URL = "http://127.0.0.1:8000";

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const res = await axios.get("http://127.0.0.1:8000/api/product/show");
        setProducts(res.data);
      } catch (err) {
        console.error("Failed to fetch products:", err);
      }
    };

    fetchProducts();
  }, []);

  return (
    <>
      <Navbar />
      <h1 className="page-title">Products</h1>
      
<div className="product-grid">
  {products.map(product => (
    <Link to={`/products/${product.id}`} key={product.id} className="product-link">
      <div className="product-card">
        <img
          src={BASE_URL + product.image}
          alt={product.title}
          className="product-image"
        />
        <div className="product-info">
          <h2 className="product-title">{product.title}</h2>
          <p className="product-desc">{product.description}</p>
          <div className="buyButton">Buy</div> {/* still styled like a button */}
          <div className="ViewButton">Buy</div> {/* still styled like a button */}
        </div>
      </div>
    </Link>
  ))}
</div>

    </>
  );
}
